import React, { useState, useEffect } from 'react';

function VideojuegosApp() {
  const [videojuegos, setVideojuegos] = useState([]);
  const [nombre, setNombre] = useState('');
  const [anio, setAnio] = useState('');
  const [idVideojuegoEdicionActual, setIdVideojuegoEdicionActual] = useState(-1);

  useEffect(() => {
    consultarTodosVideojuegos();
  }, []);

  const agregarVideojuego = async () => {
    const videojuego = { nombre: nombre, anioLanzamiento: parseInt(anio) };
    const response = await fetch("http://localhost:8080/ServicioREST/resources/Videojuego", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(videojuego),
    });

    if (response.ok) {
      const resultado = await response.json();
      setVideojuegos([...videojuegos, resultado]);
      setNombre('');
      setAnio('');
    }
  };

  const eliminarVideojuego = async (id) => {
    const response = await fetch(`http://localhost:8080/ServicioREST/resources/Videojuego/${id}`, {
      method: "DELETE",
      headers: { "Content-Type": "application/json" },
    });

    if (response.ok) {
      setVideojuegos(videojuegos.filter(videojuego => videojuego.id !== id));
    }
  };

  const consultarTodosVideojuegos = async () => {
    const response = await fetch("http://localhost:8080/ServicioREST/resources/Videojuego", {
      method: "GET",
      headers: { "Content-Type": "application/json" },
    });

    if (response.ok) {
      const resultado = await response.json();
      setVideojuegos(resultado);
    }
  };

  const llenarFormulario = (videojuego) => {
    setNombre(videojuego.nombre);
    setAnio(videojuego.anioLanzamiento.toString());
    setIdVideojuegoEdicionActual(videojuego.id);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (idVideojuegoEdicionActual === -1) {
      agregarVideojuego();
    } else {
      // L칩gica para editar un videojuego
    }
  };

  return (
    <div>
      <h1>Listado de Videojuegos</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Nombre:
          <input type="text" value={nombre} onChange={(e) => setNombre(e.target.value)} />
        </label>
        <label>
          A침o de lanzamiento:
          <input type="text" value={anio} onChange={(e) => setAnio(e.target.value)} />
        </label>
        <button type="submit">Agregar</button>
      </form>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Nombre</th>
            <th>A침o de lanzamiento</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {videojuegos.map(videojuego => (
            <tr key={videojuego.id}>
              <td>{videojuego.id}</td>
              <td>{videojuego.nombre}</td>
              <td>{videojuego.anioLanzamiento}</td>
              <td>
                <button onClick={() => llenarFormulario(videojuego)}>Editar</button>
                <button onClick={() => eliminarVideojuego(videojuego.id)}>Eliminar</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default VideojuegosApp;
